import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './personalizacao.css';

const TarefaLista = () => {
  const tarefas = [
    { id: 1, descricao: 'Preparar aula de programação' },
    { id: 2, descricao: 'Fazer feira' },
    { id: 3, descricao: 'Preparar marmitas' },
  ];

  return (
    <div className="container mt-4" style={{ borderRadius: '80px' }}>
      <table className="table table-bordered" style={{ borderRadius: '80px', border: 'none', borderSpacing: '-10px' }}>
        <tbody>
          {tarefas.map(tarefa => (
            <tr key={tarefa.id}>
              <td className="py-3 tarefa-com-espaco" style={{ margin: '10px', borderRadius: '100px', border: '2px solid transparent' }}>{tarefa.descricao}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default TarefaLista;
