import React, { useState } from 'react';
import LembreteEntrada from './LembreteEntrada';

const TarefaLista = () => {
  const [tarefas, setTarefas] = useState([]); // Initialize an empty tarefas array

  const handleAddReminder = (newReminder) => {
    setTarefas([...tarefas, { id: tarefas.length + 1, descricao: newReminder }]);
  };

  return (
    <div className="container mt-4" style={{ borderRadius: '80px' }}>
      <table className="table table-bordered" style={{ borderRadius: '80px' }}>
        <tbody>
          {/* Render both existing and new reminders */}
          {tarefas.map((tarefa) => (
            <tr key={tarefa.id}> {/* No inline style for border */}
              <td className="py-3 rounded-pill border-2">{tarefa.descricao}</td>
            </tr>
          ))}
        </tbody>
      </table>
      <LembreteEntrada onAddReminder={handleAddReminder} />
    </div>
  );
};

export default TarefaLista;
