import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import "./personalizacao.css";

//Declara a lista de tarefas fictícias
const TarefaLista = () => {
    const tarefas = [
        { id: 1, descricao: 'Preparar aula de programação' },
        { id: 2, descricao: 'Fazer feira' },
        { id: 3, descricao: 'Preparar marmitas' }
    ];
    //Retorna a lista de tarefas
    //Função map para percorrer a lista de tarefas
    return (
        <div className="container mt-4" style={{borderRadius: "80px"}}>
            <table className="table table-bordered" style={{borderRadius: "80px"}}>
                <tbody>
                    {tarefas.map(tarefa => (
                        <tr key={tarefa.id} style={{padding: "10px"}}>
                            <td className="py-3" style={{borderRadius: "25px", border: "none"}}>{tarefa.descricao}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TarefaLista;
