import 'bootstrap/dist/css/bootstrap.min.css';
import React from 'react';
import ReactDOM from 'react-dom';
import LembreteEntrada from './LembreteEntrada';
import "./personalizacao.css";

const App = () => {
    return (
        <div className="container-fluid h-100 d-flex justify-content-center align-items-center bg-dark">
            <div className="card p-4" style={{ maxWidth: '500px' }}>
                <h1 className="display-4 text-center text-white mb-4">Meus Lembretes</h1>
                <div className="card-body">
                    <div className="mb-3">
                        <label htmlFor="lembreteInput" className="form-label text-white">Novo Lembrete</label>
                        <LembreteEntrada />
                    </div>
                </div>
            </div>
        </div>
    );
}

ReactDOM.render(
    <App />,
    document.querySelector('#root')
);
