import 'bootstrap/dist/css/bootstrap.min.css'
import React from 'react'
import ReactDOM from 'react-dom'
import LembreteEntrada from './LembreteEntrada'
import "./personalizacao.css";

const App = () => {
    return (
        // Container principal
        <div className="container-fluid bg-primary vh-100 d-flex justify-content-center align-items-center">
            {/* Conteúdo centralizado */}
            <div className="card p-5">
                {/* Título */}
                <h1 className="display-4 text-center text-white mb-5">Meus Lembretes</h1>
                {/* Formulário de entrada de lembretes */}
                <div className="row justify-content-center">
                    <div className="col-md-8">
                        <div className="card bg-light p-4">
                            <div className="card-body">
                                <LembreteEntrada />
                            </div>
                        </div>
                    </div>
                </div>
            </div>
        </div>
    );
}

ReactDOM.render(
    <App />,
    document.querySelector('#root')
)
