import React from 'react';
import 'bootstrap/dist/css/bootstrap.min.css'
import "./personalizacao.css";
import LembreteEntrada from './LembreteEntrada';

const TarefaLista = () => {
    const tarefas = [
        { id: 1, descricao: 'Preparar aula de programação' },
        { id: 2, descricao: 'Fazer feira' },
        { id: 3, descricao: 'Preparar marmitas' }
    ];

    return (
        <div className="container mt-4" style={{borderRadius: "80px"}}>
            <table className="table table-bordered" style={{borderRadius: "80px", border: "none"}}>
                <tbody>
                    {tarefas.map(tarefa => (
                        <tr key={tarefa.id} style={{border: "none"}}>
                            <td className="py-3" style={{borderRadius: "100px", border: "2px"}}>{tarefa.descricao}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default TarefaLista;
