import React, { useState } from 'react';
import LembreteEntrada from './LembreteEntrada';

const TarefaLista = () => {
  const [tarefas, setTarefas] = useState([
    { id: 1, descricao: 'Preparar aula de programação' },
    { id: 2, descricao: 'Fazer feira' },
    { id: 3, descricao: 'Preparar marmitas' },
  ]);

  const handleAddReminder = (newReminder) => {
    setTarefas([...tarefas, { id: tarefas.length + 1, descricao: newReminder }]);
  };

  return (
    <div className="container mt-4" style={{ borderRadius: "80px" }}>
      <table className="table table-bordered" style={{ borderRadius: "80px", border: "none" }}>
        <tbody>
          {/* Render both existing and new reminders */}
          {tarefas.map((tarefa) => (
            <tr key={tarefa.id} style={{ border: "none" }}>
              <td className="py-3" style={{ borderRadius: "100px", border: "2px" }}>
                {tarefa.descricao}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
      <LembreteEntrada onAddReminder={handleAddReminder} /> {/* Pass the prop */}
    </div>
  );
};

export default TarefaLista;
