import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';
import './personalizacao.css';
import LembreteEntrada from './LembreteEntrada';

const TarefaLista = () => {
  const [tarefas, setTarefas] = useState([
    { id: 1, descricao: 'Preparar aula de programação' },
    { id: 2, descricao: 'Fazer feira' },
    { id: 3, descricao: 'Preparar marmitas' },
  ]);

  const [novaTarefa, setNovaTarefa] = useState('');

  const handleNovaTarefaChange = (event) => {
    setNovaTarefa(event.target.value);
  };

  const adicionarNovaTarefa = () => {
    if (novaTarefa !== '') {
      setTarefas([...tarefas, { id: tarefas.length + 1, descricao: novaTarefa }]);
      setNovaTarefa('');
    }
  };

  return (
    <div className="container mt-4" style={{ borderRadius: '80px' }}>
      <table className="table table-bordered" style={{ borderRadius: '80px', border: 'none' }}>
        <tbody>
          {tarefas.map((tarefa) => (
            <tr key={tarefa.id} style={{ border: 'none' }}>
              <td className="py-3" style={{ borderRadius: '100px', border: '2px' }}>{tarefa.descricao}</td>
            </tr>
          ))}
        </tbody>
      </table>

      <div className="mt-4">
        <h2>Adicionar Nova Tarefa</h2>
        <input
          type="text"
          placeholder="Digite a nova tarefa..."
          value={novaTarefa}
          onChange={handleNovaTarefaChange}
        />
        <button onClick={adicionarNovaTarefa}>Adicionar</button>
      </div>
    </div>
  );
};

export default TarefaLista;
