import React, { useState } from 'react';
import 'bootstrap/dist/css/bootstrap.min.css';

function LembreteEntrada() {
    const [lembrete, setLembrete] = useState('');
    const [lembretes, setLembretes] = useState([]);

    const handleInputChange = (event) => {
        setLembrete(event.target.value);
    };

    const adicionarLembrete = () => {
        if (lembrete.trim() !== '') { // Verifica se o lembrete não está vazio ou contém apenas espaços em branco
            setLembretes([...lembretes, lembrete]);
            setLembrete('');
        }
    };

    return (
        <div className="d-flex flex-column align-items-center">
            <h1 className="text-center mb-4">Adicionar Lembrete</h1>
            <div className="input-group mb-3">
                <input
                    className="form-control"
                    type="text"
                    value={lembrete}
                    onChange={handleInputChange}
                    placeholder="Digite um lembrete..."
                />
                <button
                    className="btn btn-success"
                    type="button"
                    onClick={adicionarLembrete}
                >
                    Adicionar
                </button>
            </div>
            <table className="table table-striped table-bordered table-light">
                <thead>
                    <tr>
                        <th>Lembrete:</th>
                    </tr>
                </thead>
                <tbody>
                    {lembretes.map((item, index) => (
                        <tr key={index}>
                            <td>{item}</td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
}

export default LembreteEntrada;
